    function cleanHtml(html)
    {
      return html
        .replace(/<\/?p[^>]*>/g, '\n')
        .replace(/<\/?strong>/g, '**')
        .replace(/<\/?em>/g, '_')
        .replace(/<\/?br>/g, '\n')
        .replace(/<\/?ul>/g, '')
        .replace(/<\/?li>/g, '- ')
        .replace(/<\/?span[^>]*>/g, '')
        .replace(/<\/?div[^>]*>/g, '')
        .replace(/<\/?img[^>]*>/g, '')
        .replace(/&nbsp;/g, ' ')
        .replace(/&quot;/g, '"')
        .replace(/&ldquo;/g, '"')
        .replace(/&rdquo;/g, '"')
        .replace(/\n\s*\n/g, '\n\n')
        .trim();
    }

